body.script:
// ðŸ”‘ YOUR RAW GITHUB LINK
const FILE_URL = 'https://raw.githubusercontent.com/Zhacks3736/Games/main/1.xml'; 

// Function to load the game automatically
function loadGame() {
    const iframe = document.getElementById("fr");

    fetch(FILE_URL)
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            return response.text();
        })
        .then(text => {
            iframe.contentDocument.open();
            iframe.contentDocument.write(text);
            iframe.contentDocument.close();

            iframe.style.display = "block";
        })
        .catch(err => {
            console.error('Error loading game content:', err);
            alert('Error: Could not load the game module.');
        });
}

// Function for the fullscreen button
function openFullscreen() {
    fetch(FILE_URL)
        .then(response => response.text())
        .then(text => {
            const newWindow = window.open("", "_blank");
            if (newWindow) {
                newWindow.document.open();
                newWindow.document.write(text);
                newWindow.document.close();
            } else {
                alert('Full screen failed: Pop-up blocked or failed to open.');
            }
        })
        .catch(err => {
            console.error('Error opening fullscreen:', err);
        });
}

// Automatically call loadGame() when the page is fully loaded
window.addEventListener('load', loadGame);
